
/**
 * mobile menu start
 * active - class added in the menu bar for responsive view.
 */

 $(document).ready(function(){
    $(".menu-btn").click(function(){
      $('.nav-menu').toggleClass('active');
    });

    $(".icon-menu-icon").click(function(){
      $(this).hide();
      $('.icon-close-icon').show();
    })
    $(".icon-close-icon").click(function(){
      $(this).hide();
      $('.icon-menu-icon').show();
  })
    $('.dropdown').click(function(){
       $('.dropdown').children(".sub-menu").toggleClass('active-sub');
       $('.dropdown').find(".dropdown-icon").toggleClass('drop-icon');
    })
  });

// mobile menu end

// aos Animation start

  AOS.init();

// aos Animation end